function toggleDarkMode(){let e=document.body,d=e.classList.toggle("dark-mode");localStorage.setItem("darkMode",d)}function loadDarkModePreference(){let e="true"===localStorage.getItem("darkMode");e&&document.body.classList.add("dark-mode")}function setupDarkModeToggle(){let e=document.getElementById("darkModeToggle");e&&e.addEventListener("change",toggleDarkMode)}document.addEventListener("DOMContentLoaded",()=>{loadDarkModePreference(),setupDarkModeToggle()});