function toggleDarkMode(){const e=document.body.classList.toggle("dark-mode");localStorage.setItem("darkMode",e)}function loadDarkModePreference(){"true"===localStorage.getItem("darkMode")&&document.body.classList.add("dark-mode")}function setupDarkModeToggle(){const e=document.getElementById("darkModeToggle");e&&e.addEventListener("change",toggleDarkMode)}document.addEventListener("DOMContentLoaded",(()=>{loadDarkModePreference(),setupDarkModeToggle()}));